INSERT INTO `type` (`name`, `description`) VALUES
('Ambrées', 'Une bière ambrée est une bière de couleur roux clair, brassée à partir de malt non torréfié et de malt torréfié. La couleur et le goût varient suivant la quantité et la durée de torréfaction du malt.'),
('Blanches', 'Les bières blanches sont des bières brassées avec une forte proportion de froment, malté ou non. Il existe deux grandes traditions de bière « blanche » : en Belgique et en Bavière. '),
('Blondes', "Une bière blonde est une bière de couleur claire. Il ne s'agit pas d'un type de bières à proprement parler, qui correspond à un procédé de fabrication, de brassage ou de fermentation."),
('Brunes', 'Brassage avec des malts bruns, appelés malts “chocolat”fortement torréfiés qui leur confèrent un fort potentiel aromatique et des couleurs allant du marron au noir')